import { useState } from "react";
import axios from "../services/api";

const BookForm = () => {
  const [book, setBook] = useState({ title: "", author: "", isbn: "", published_date: "" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("books/create", book, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("Book added successfully!");
    } catch (error) {
      alert("Failed to add book!");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Title" className="form-control mb-2" onChange={(e) => setBook({ ...book, title: e.target.value })} />
      <input type="text" placeholder="Author" className="form-control mb-2" onChange={(e) => setBook({ ...book, author: e.target.value })} />
      <input type="text" placeholder="ISBN" className="form-control mb-2" onChange={(e) => setBook({ ...book, isbn: e.target.value })} />
      <input type="date" className="form-control mb-2" onChange={(e) => setBook({ ...book, published_date: e.target.value })} />
      <button type="submit" className="btn btn-success">Add Book</button>
    </form>
  );
};

export default BookForm;
