import { useState, useEffect } from "react";
import api, { getCsrfToken } from "../services/api";  // Import Axios instance
import { useNavigate } from "react-router-dom";

const AdminSignup = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/api/csrf/"); // ✅ Request CSRF token when component loads
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      await api.post("/admin/signup/", formData, {
        headers: {
          "X-CSRFToken": getCsrfToken(),  // ✅ Include CSRF token
        },
      });

      alert("Admin created successfully! Redirecting to login...");
      navigate("/login"); // Redirect to login page
    } catch (err) {
      console.error("Signup Error:", err);
      setError(err.response?.data?.error || "Signup failed! Try again.");
    }
  };

  return (
    <div className="container mt-5 col-md-4">
      <h2>Admin Signup</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          className="form-control mb-2"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          className="form-control mb-2"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          required
        />
        <button type="submit" className="btn btn-primary w-100">
          Signup
        </button>
      </form>
    </div>
  );
};

export default AdminSignup;
