import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Navbar = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("access_token");
    setIsAuthenticated(!!token); // Check if user is logged in
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("access_token");
    localStorage.removeItem("refresh_token");
    setIsAuthenticated(false);
    navigate("/login");
  };

  return (
    <nav className="navbar navbar-dark bg-dark p-3">
      <Link className="navbar-brand" to="/">Book Management</Link>
      <div className="dropdown">
        <button 
          className="btn btn-light dropdown-toggle" 
          onClick={() => setIsDropdownOpen(!isDropdownOpen)}
        >
          Menu
        </button>
        {isDropdownOpen && (
          <div className="dropdown-menu show" style={{ right: 0, left: "auto" }}>
            {!isAuthenticated && <Link className="dropdown-item" to="/signup">Signup</Link>}
            {isAuthenticated && <button className="dropdown-item" onClick={handleLogout}>Logout</button>}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
